    public DatabaseHandler(Context context) {
        super(context, DATABASE_NAME, null, DATABASE_VERSION);
        Log.d(LOG_TAG, "Database successfully initialised: " + getDatabaseName());
    }

    @Override
    public void onCreate(SQLiteDatabase db) {
        try {
            db.execSQL(SQL_CREATE_FAVORITES_TABLE);
            db.execSQL(SQL_CREATE_CUSTOM_TABLE);
            db.execSQL(SQL_CREATE_MAIN_TABLE);
            db.execSQL(SQL_CREATE_PLAYLIST_TABLE);
            db.execSQL(SQL_CREATE_PLAYLIST_SOUND_TABLE);
        }catch (Exception e){
            Log.e(LOG_TAG, " " +e.getMessage());
        }
    }

    @Override
    public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {
        if(oldVersion < 19){
            db.execSQL("DROP TABLE IF EXISTS " + MAIN_TABLE);
            db.execSQL("DROP TABLE IF EXISTS " + FAVORITES_TABLE);
        }
        db.execSQL("DROP TABLE IF EXISTS " + PLAYLIST_SOUND_TABLE);
        onCreate(db);
    }

    private boolean verification(SQLiteDatabase database, Integer soundId){
        int count = -1;
        Cursor cursor = null;
        try {
            String query = "SELECT * FROM " + DatabaseHandler.FAVORITES_TABLE + " WHERE " + DatabaseHandler.FAVORITES_ITEM_ID + " = " +  +soundId ;
            cursor = database.rawQuery(query, null);
            if (cursor.moveToFirst()){
                count = cursor.getInt(0);
            }
            return (count > 0);
        } finally {
            if (cursor != null){
                cursor.close();
            }
        }
    }

