    public void addPlaylist(String namePlaylist) {
        try (SQLiteDatabase database = this.getWritableDatabase()) {
            ContentValues contentValues = new ContentValues();
            contentValues.put(PLAYLIST_NAME, namePlaylist);
            database.insert(PLAYLIST_TABLE, null, contentValues);
        } catch (Exception e) {
            Log.e(LOG_TAG, "(FAVORIS) Erreur lors de la cration de la playlist : " + e.getMessage());
        }
    }

    public void addToPlaylist(SoundObject soundObject, int idPlaylist) {
        SQLiteDatabase database = this.getWritableDatabase();
        try{
            ContentValues contentValues = new ContentValues();
            contentValues.put(PLAYLIST_SOUND_SOUND_ID, soundObject.getNameImage());
            contentValues.put(PLAYLIST_SOUND_PLAYLIST_ID, idPlaylist);
            database.insert(PLAYLIST_SOUND_TABLE, null, contentValues);
        } catch (Exception e){
            Log.e(LOG_TAG, "(FAVORIS) Erreur lors de l'insertion du son dans la playlist: " + e.getMessage());
        } finally {
                database.close();
        }
    }

    public void deleteOfPlaylist(SoundObject soundObject,Context context) {
        try (SQLiteDatabase database = this.getWritableDatabase()) {
            String selection = PLAYLIST_SOUND_SOUND_ID + " = ?";
            String[] selectionArgs = { String.valueOf(soundObject.getNameImage()) };
            database.delete(PLAYLIST_SOUND_TABLE, selection, selectionArgs);
            Activity activity = (Activity) context;
            Intent intent = activity.getIntent();
            activity.overridePendingTransition(0, 0);
            intent.addFlags(Intent.FLAG_ACTIVITY_NO_ANIMATION);
            activity.finish();
            activity.overridePendingTransition(0, 0);
            context.startActivity(intent);
        } catch (Exception e) {
            Log.e(LOG_TAG, "(PLAYLIST) Erreur lors de la suppression du son: " + e.getMessage());
        }
    }


    public void addFavorite(SoundObject soundObject) {
        SQLiteDatabase database = this.getWritableDatabase();
        if (!verification(database, soundObject.getItemID()) ) {
            try{
                ContentValues contentValues = new ContentValues();
                contentValues.put(FAVORITES_NAME, soundObject.getItemName());
                contentValues.put(FAVORITES_ITEM_ID, soundObject.getItemID());
                contentValues.put(FAVORITES_ITEM_IMAGE, soundObject.getNameImage());
                database.insert(FAVORITES_TABLE, null, contentValues);
            } catch (Exception e){
                Log.e(LOG_TAG, "(FAVORIS) Erreur lors de l'insertion des sons: " + e.getMessage());
            } finally {
                database.close();
            }
        } else {
            Log.e(LOG_TAG, "(FAVORIS) Erreur lors de la vrification des sons: ");
        }
    }

